insert.foreach:
INSERT INTO REPORT_ZJ_BATCH_BACUK ( PRI_NUMBER ,REQUEST_NO , BATCH_NO ,INTERFACE_CONTENT , CREATE_DATE , CREATE_NAME
)
VALUES ( #{item.priNumber} ,#{item.requestNo} , #{item.batchNo} ,#{item.interfaceContent} , #{item.createDate} ,#{item.createName} )

mapper.select:
SELECT li.pri_number AS priNumber , li.loan_contract_no AS loanContractNo , li.seq AS seq , li.interface_content AS interfaceContent , li.create_date AS createDate , li.create_name AS createName , li.status AS status ,li.branch_org as branchOrg , li.loan_name as loanName FROM REPORT_INTERFACE_LOG_BACK li
WHERE 1=1 and RESLOVE_FLAG = '0' and FLAG = '1'
AND to_char(li.create_date,'yyyy-mm-dd') = #{parameter.synDate}

update.foreach:

UPDATE REPORT_INTERFACE_LOG_BACK 
SET RESLOVE_FLAG = '1' 
WHERE  pri_number = #{item.priNumber}
